mapper.update:
update euicc_profile
set state = #{state,jdbcType=VARCHAR}
where eid = #{eid,jdbcType=VARCHAR}

update euicc_profile
set pol2_id = #{pol2Id,jdbcType=VARCHAR}
where iccid = #{iccid,jdbcType=VARCHAR}

mapper.select:
select
,
from euicc_profile where state = #{state,jdbcType=VARCHAR} and eid = #{eid,jdbcType=VARCHAR}

select
,
from euicc_profile where eid = #{eid,jdbcType=VARCHAR} and fallback_attribute = #{fallbackAttribute,jdbcType=VARCHAR}
